import React, { useEffect, useState } from 'react';
import API from './api';
import TransactionForm from './components/TransactionForm';
import TransactionList from './components/TransactionList';

export default function App() {
  const [transactions, setTransactions] = useState([]);
  const [summary, setSummary] = useState({ income:0, expenses:0, balance:0 });

  const fetchAll = async () => {
    try {
      const [tRes, sRes] = await Promise.all([
        API.get('/transactions'),
        API.get('/summary')
      ]);
      setTransactions(tRes.data);
      setSummary(sRes.data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => { fetchAll(); }, []);

  const addTransaction = async (data) => {
    try {
      await API.post('/transactions', data);
      await fetchAll();
    } catch (err) { console.error(err); }
  };

  const updateTransaction = async (id, data) => {
    try {
      await API.put('/transactions/' + id, data);
      await fetchAll();
    } catch (err) { console.error(err); }
  };

  const deleteTransaction = async (id) => {
    try {
      await API.delete('/transactions/' + id);
      await fetchAll();
    } catch (err) { console.error(err); }
  };

  return (
    <div className="container">
      <h1 className="mb-4">Expense Tracker</h1>

      <div className="row mb-4">
        <div className="col-md-4">
          <div className="card p-3">
            <h5>Summary</h5>
            <p>Income: <strong>₹{summary.income.toFixed(2)}</strong></p>
            <p>Expenses: <strong>₹{summary.expenses.toFixed(2)}</strong></p>
            <p>Balance: <strong>₹{summary.balance.toFixed(2)}</strong></p>
          </div>
          <div className="mt-3 card p-3">
            <TransactionForm onSave={addTransaction} />
          </div>
        </div>
        <div className="col-md-8">
          <div className="card p-3 card-scroll">
            <TransactionList transactions={transactions} onDelete={deleteTransaction} onUpdate={updateTransaction} />
          </div>
        </div>
      </div>
    </div>
  );
}
