import React, { useEffect, useState } from 'react';
import API from './api';
import TransactionForm from './components/TransactionForm';
import TransactionList from './components/TransactionList';

export default function App() {
  const [transactions, setTransactions] = useState([]);
  const [summary, setSummary] = useState({ income: 0, expenses: 0, balance: 0 });
  const [loading, setLoading] = useState(true);

  const fetchAll = async () => {
    setLoading(true);
    try {
      const [tRes, sRes] = await Promise.all([
        API.get('/transactions'),
        API.get('/summary')
      ]);
      setTransactions(tRes.data);
      setSummary(sRes.data);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => { fetchAll(); }, []);

  const addTransaction = async (data) => {
    try {
      await API.post('/transactions', data);
      await fetchAll();
    } catch (err) { 
      console.error(err);
      // You could add a toast notification here
    }
  };

  const updateTransaction = async (id, data) => {
    try {
      await API.put('/transactions/' + id, data);
      await fetchAll();
    } catch (err) { 
      console.error(err);
      // You could add a toast notification here
    }
  };

  const deleteTransaction = async (id) => {
    try {
      await API.delete('/transactions/' + id);
      await fetchAll();
    } catch (err) { 
      console.error(err);
      // You could add a toast notification here
    }
  };

  // Format currency
  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-PK', {
      style: 'currency',
      currency: 'PKR',
      minimumFractionDigits: 2
    }).format(amount);
  };

  return (
    <div className="app-container">
      {/* Navbar */}
      <nav className="navbar navbar-dark bg-gradient">
        <div className="container-fluid">
          <div className="d-flex align-items-center">
            <div className="navbar-brand d-flex align-items-center">
              <div className="logo-icon me-3">
                <i className="fas fa-wallet"></i>
              </div>
              <div>
                <h1 className="mb-0">Expense Tracker</h1>
                <small className="text-white-50">Manage your finances efficiently</small>
              </div>
            </div>
          </div>
        </div>
      </nav>

      {/* Main Content */}
      <main className="main-content">
        <div className="container-fluid">
          <div className="row">
            {/* Sidebar */}
            <div className="col-md-3 sidebar">
              {/* Summary Card */}
              <div className="card summary-card mb-4 glass-effect">
                <div className="card-body">
                  <div className="d-flex align-items-center mb-4">
                    <div className="summary-icon me-3">
                      <i className="fas fa-chart-pie"></i>
                    </div>
                    <h5 className="card-title mb-0">Financial Summary</h5>
                  </div>

                  <div className="summary-item income">
                    <div className="d-flex justify-content-between align-items-center">
                      <div>
                        <div className="d-flex align-items-center">
                          <i className="fas fa-arrow-up income-icon me-2"></i>
                          <span>Income</span>
                        </div>
                      </div>
                      <span className="income-amount">{formatCurrency(summary.income)}</span>
                    </div>
                  </div>

                  <div className="summary-item expense">
                    <div className="d-flex justify-content-between align-items-center">
                      <div>
                        <div className="d-flex align-items-center">
                          <i className="fas fa-arrow-down expense-icon me-2"></i>
                          <span>Expenses</span>
                        </div>
                      </div>
                      <span className="expense-amount">{formatCurrency(summary.expenses)}</span>
                    </div>
                  </div>

                  <div className="divider"></div>

                  <div className="balance-section">
                    <div className="d-flex justify-content-between align-items-center">
                      <div>
                        <div className="d-flex align-items-center">
                          <i className="fas fa-balance-scale balance-icon me-2"></i>
                          <span>Balance</span>
                        </div>
                      </div>
                      <span className={`balance-amount ${summary.balance >= 0 ? 'positive' : 'negative'}`}>
                        {formatCurrency(summary.balance)}
                      </span>
                    </div>
                  </div>
                </div>
              </div>

              {/* Add Transaction Card */}
              <div className="card add-transaction-card glass-effect">
                <div className="card-body">
                  <div className="d-flex align-items-center mb-4">
                    <div className="add-icon me-3">
                      <i className="fas fa-plus-circle"></i>
                    </div>
                    <h5 className="card-title mb-0">Add Transaction</h5>
                  </div>
                  <TransactionForm onSave={addTransaction} />
                </div>
              </div>
            </div>

            {/* Main Content */}
            <div className="col-md-9 content-area">
              <div className="card transactions-card glass-effect">
                <div className="card-body">
                  <div className="d-flex justify-content-between align-items-center mb-4">
                    <div className="d-flex align-items-center">
                      <div className="transactions-icon me-3">
                        <i className="fas fa-list-alt"></i>
                      </div>
                      <h4 className="card-title mb-0">Transactions</h4>
                    </div>
                    <div className="transaction-count">
                      <span className="badge bg-primary rounded-pill">
                        {transactions.length} {transactions.length === 1 ? 'Transaction' : 'Transactions'}
                      </span>
                    </div>
                  </div>

                  {loading ? (
                    <div className="text-center py-5">
                      <div className="spinner-border text-primary" role="status">
                        <span className="visually-hidden">Loading...</span>
                      </div>
                      <p className="mt-3">Loading transactions...</p>
                    </div>
                  ) : (
                    <TransactionList
                      transactions={transactions}
                      onDelete={deleteTransaction}
                      onUpdate={updateTransaction}
                    />
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      </main>

      {/* Footer */}
      <footer className="bg-light text-center py-4 mt-4 border-top">
        <div className="container">
          <div className="d-flex justify-content-between align-items-center">
            <div>
              <small>Â© {new Date().getFullYear()} Expense Tracker</small>
            </div>
            <div>
              <small>Built with React + Node.js</small>
